        val expPaymentTokenAddress = Address.apply("0x7ceb23fd6bc0add59e62ac25578270cff1b9f619")
        assertThat(result)
            .hasFieldOrPropertyWithValue(ZeroExMatchOrdersData::leftOrder.name, expLeftOrder)
